import "./App.css";
import { useState } from "react";
import {Tetst} from './tetst';

function App() {
  const [messageList, setMessage] = useState([]);

  return (
    <div className="App">
      <h1>
        <SendMessage setMessage={setMessage} />
        {messageList.map((e) => (
          <div key={new Date()}>{e}</div>
        ))}
        <Tetst defoltNumber/>
      </h1>

      <header className="App-header">
        <div className="block">
        </div>
      </header>
    </div>
  );
}

export default App;

const SendMessage = ({ setMessage }) => {
  const [text, setText] = useState("");
  const [author, setAuthor] = useState("");

  return (
    <div>
      <input
        type="text"
        value={text}
        onChange={(e) => {
          setText(e.target.value);
        }}
      />
      <input
        type="text"
        value={author}
        onChange={(e) => {
          setAuthor(e.target.value);
        }}
      />
      <button
        onClick={() => {
          setMessage((p) => [...p, text, author]);
        }}
      >
        Отправить в интернеты
      </button>
    </div>
  );
};
