import "./App.css";
import { useState } from "react";

function App() {
  const userName = "Panda";
  const textForTexty = "Everything is going to be good!";
  const [messageList, setMessages] = useState([]);

  return (
    <div className="App">
      <Myheader name={userName} />
      <Message texty={textForTexty} />
      <NewComponent />
      <h1>
        <SendMessage setMessage={setMessages} />
        {messageList.map((e) => (
          <div>{e}</div>
        ))}
      </h1>

      <header className="App-header">
        <div className="block">
          Hello!
          <br />
          <button
            className="button"
            autoFocus
            onClick={() => alert("You got two termal clips!")}
          >
            Press on this button to became a programmer!
          </button>
        </div>
      </header>
    </div>
  );
}

export default App;

// Добавить в компонент App поле стейта а messageList. В нем хранить массив объектов -
// сообщений (объект должен содержать, как минимум, поля text и author). Начальное
// значение - пустой массив).

const SendMessage = ({ setMessages }) => {
  const [text, setText] = useState("");
  const [author, setAuthor] = useState("");

  return (
    <div>
      <input
        type="text"
        value={text}
        // А onChange - действие в момент изменений в инпуте мы передаем функцию
        // через которую состояние меняем (не состояние родителя а внутреннее состояние
        //   компонента)
        onChange={(e) => {
          setText(e.target.text);
        }}
      />
      <input
        type="text"
        value={author}
        // А onChange - действие в момент изменений в инпуте мы передаем функцию
        // через которую состояние меняем (не состояние родителя а внутреннее состояние
        //   компонента)
        onChange={(e) => {
          setAuthor(e.target.author);
        }}
      />
      <button
        onClick={() => {
          setMessages((p) => [...p, text, author]);
        }}
      >
        Отправить в интернеты
      </button>
    </div>
  );
};

