export const SendMessage = ({ setMessage }) => {
  const [text, setText] = useState("");
  const [author, setAuthor] = useState("");

  return (
    <div>
      <input
        type="text"
        value={text}
        onChange={(e) => {
          setText(e.target.value);
        }}
      />
      <input
        type="text"
        value={author}
        onChange={(e) => {
          setAuthor(e.target.value);
        }}
      />
      <button
        onClick={() => {
          setMessage((p) => [...p, text, author]);
        }}
      >
        Отправить в интернеты
      </button>
    </div>
  );
};
