import "./App.css";
import  { useState } from "react";

function App() {
  const userName = "Panda";
  const textForTexty = "Everything is going to be good!";
  const [messageList, setMessages] = useState([])

  return (
    <div className="App">
      <Myheader name={userName} />
      <Message texty={textForTexty} />
      <NewComponent/>
      <>
        <SendMessage setMessage={setMessages}/>
      {messageList.map((e) => (
        <div>{e}</div>
      ))}
      </>

      <header className="App-header">
        <div className="block">
          Hello!
          <br />
          <button
            className="button"
            autoFocus
            onClick={() => alert("You got two termal clips!")}
          >
            Press on this button to became a programmer!
          </button>
        </div>
      </header>
    </div>
  );
}

export default App;

// Добавить в компонент App поле стейта а messageList. В нем хранить массив объектов -
// сообщений (объект должен содержать, как минимум, поля text и author). Начальное
// значение - пустой массив).

const SendMessage = ({ setMessages }) => {
  const [value, setValue] = useState("")

  return (
    <div>

    </div>
  )
}

const Myheader = (props) => {
  return (
    <div className="panda">
      <h1 className="panda_h1">Hello, {props.name}</h1>
    </div>
  );
};

const Message = (texty) => {
  return (
    <div className="texty">
      <h2> You are the best! {texty.textForTexty} </h2>
    </div>
  );
};

const NewComponent = (prop) => {
  const [user, setUser] = useState({
    name: "Alex",
  });

  console.log("render", user);

  return (
    <div>
      {user.name}
      <button
        onClick={() => {
          setUser(pervstate => ({ ...pervstate, agree: true }));
        }}
      >
        Click
      </button>
    </div>
  );
};
