import "./App.css";
import { useState } from "react";

function App() {
  const [messageList, setMessage] = useState([]);

  return (
    <div className="App">
      <h1>
        <SendMessage setMessage={setMessage} />
        {messageList.map((e) => (
          <div>{e}</div>
        ))}
      </h1>

      <header className="App-header">
        <div className="block">
        </div>
      </header>
    </div>
  );
}

export default App;

// Добавить в компонент App поле стейта а messageList. В нем хранить массив объектов -
// сообщений (объект должен содержать, как минимум, поля text и author). Начальное
// значение - пустой массив).

const SendMessage = ({ setMessage }) => {
  const [text, setText] = useState("");
  const [author, setAuthor] = useState("");

  return (
    <div>
      <input
        type="text"
        value={text}
        // А onChange - действие в момент изменений в инпуте мы передаем функцию
        // через которую состояние меняем (не состояние родителя а внутреннее состояние
        //   компонента)
        onChange={(e) => {
          setText(e.target.text);
        }}
      />
      <input
        type="text"
        value={author}
        // А onChange - действие в момент изменений в инпуте мы передаем функцию
        // через которую состояние меняем (не состояние родителя а внутреннее состояние
        //   компонента)
        onChange={(e) => {
          setAuthor(e.target.author);
        }}
      />
      <button
        onClick={() => {
          setMessage((p) => [...p, text, author]);
        }}
      >
        Отправить в интернеты
      </button>
    </div>
  );
};

